import React, { useState } from "react";
import "../styles/Contact.css";

const Contact = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    telephone: "",
    message: "",
  });
  const [status, setStatus] = useState("");

  const handleChange = (e) => {
    setFormData((prev) => ({
      ...prev,
      [e.target.name]: e.target.value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setStatus("Sending...");
    setTimeout(() => {
      setStatus("Thank you for reaching out! We're thrilled that you're interested in LinkToDigital. Our team will review your message and get back to you as soon as possible.");
      setFormData({ name: "", email: "", telephone: "", message: "" });
    }, 1000);
  };

  return (
    <div className="contact-bg">
      <div className="contact-container">
        <h2>Contact Us</h2>
        <form className="contact-form" onSubmit={handleSubmit}>
          <label htmlFor="name">Your name</label>
          <input
            type="text"
            id="name"
            name="name"
            placeholder="Your name"
            value={formData.name}
            onChange={handleChange}
            required
          />
          <label htmlFor="email">Your email</label>
          <input
            type="email"
            id="email"
            name="email"
            placeholder="Your email"
            value={formData.email}
            onChange={handleChange}
            required
          />
          <label htmlFor="telephone">Your telephone</label>
          <input
            type="tel"
            id="telephone"
            name="telephone"
            placeholder="Your telephone"
            value={formData.telephone}
            onChange={handleChange}
            pattern="[0-9\-\+\s()]{7,20}"
          />
          <label htmlFor="message">Your message</label>
          <textarea
            id="message"
            name="message"
            placeholder="Your message"
            value={formData.message}
            onChange={handleChange}
            required
          />
          <button type="submit">Submit</button>
        </form>
        {status && <p className="contact-status">{status}</p>}
      </div>
    </div>
  );
};

export default Contact;
